INSERT INTO `dc_posts` VALUES (2865, 1, '2018-08-24 16:26:25', '2018-08-24 13:26:25', 'This year The Austin Game Conference management team has partnered with the International Game Developers Association (IGDA) to bring you the IDGA Game Leadership Summit, taking place September 13-14, 2018 at the Austin Convention Center. The Leadership Summit replaces AGC this year. Join us as 25 speakersin 2 tracks  Leadership and Management  coach on you on building and leading great gamedev teams.', 'IGDA GAME LEADERSHIP SUMMIT', 'This year The Austin Game Conference management team has partnered with the International Game Developers Association (IGDA) to bring you the IDGA Game Leadership Summit, taking place September 13-14, 2018 at the Austin Convention Center. The Leadership Summit replaces AGC this year. Join us as 25 speakersin 2 tracks  Leadership and Management  coach on you on building and leading great gamedev teams.', 'inherit', 'closed', 'closed', '', '2835-revision-v1', '', '', '2018-08-24 16:26:25', '2018-08-24 13:26:25', '', 2835, 'https://dailyconf.com/2835-revision-v1/', 0, 'revision', '', 0); 
