INSERT INTO `dc_posts` VALUES (2974, 1, '2018-09-03 13:02:36', '2018-09-03 10:02:36', 'DreamHack is the worlds largest digital festival, a meeting place for gamers, with major events hosted each year in Europe and North America. Founded in 1994, DreamHacks core has always been the LAN party where attendees bring their computer from home and set up for 3 straight days of gaming. DreamHacks festivals have grown organically over time, becoming a platform for esports, Internet and gaming culture, intellectual and creative competitions, live music acts, lectures by industry experts, celebrity content creators, cosplayers, DreamExpo Hall and much more.\r\nDreamHack is also a production company, creating content focused on gaming, esports, music and arena events, for both traditional Television and digital content networks.\r\n\r\nIn 2017, DreamHack welcomed more than 250,000 visitors to its live events and its online esports broadcasts received more than 375 million views.\r\n\r\n[embed width="640" height="360"]https://youtu.be/Eib7RgU5xNc\r\n[/embed]', 'DreamHack', '', 'inherit', 'closed', 'closed', '', '2808-revision-v1', '', '', '2018-09-03 13:02:36', '2018-09-03 10:02:36', '', 2808, 'https://dailyconf.com/2808-revision-v1/', 0, 'revision', '', 0); 
