INSERT INTO `dc_posts` VALUES (2983, 1, '2018-09-03 13:37:32', '2018-09-03 10:37:32', 'The Game Awards recognizes and upholds creative and technical excellence in the global video game industry. We bring together a diverse group of game players, game developers, and notable names from popular culture to celebrate and advance gamings position as the most immersive, challenging and inspiring form of entertainment.\n\nOur Awards ceremony, held annually in December, celebrates both game creators and game players, and gives fans a sneak preview of games set to be released in the future. The program is broadcast globally, free-of-charge, on all gaming screens and digital platforms, and recognizes video games across PC, mobile, console, VR/AR, and also eSports.', 'The game awards', '', 'inherit', 'closed', 'closed', '', '2964-autosave-v1', '', '', '2018-09-03 13:37:32', '2018-09-03 10:37:32', '', 2964, 'https://dailyconf.com/2964-autosave-v1/', 0, 'revision', '', 0); 
