INSERT INTO `dc_posts` VALUES (2991, 1, '2018-09-03 13:43:42', '2018-09-03 10:43:42', 'PAX Unplugged will be an exciting analog-focused extension of our already existing portfolio of PAX events. \n\nWeve found that the Tabletop parts of PAX have grown and grown to the point where we said, You know what? Lets make a show thats just that.PAX Unplugged is an event specifically tailored to gamers of all ages. All of the content throughout the show is meant to be enjoyed by the entire family all weekend and whats more, Sundays offerings are specifically set up to appeal to our younger gamers in attendance. \n\nIts with that in mind that we will also be offering a Sunday Kids Day badge for anyone under 12.', 'PAX Unplugged', '', 'inherit', 'closed', 'closed', '', '2963-autosave-v1', '', '', '2018-09-03 13:43:42', '2018-09-03 10:43:42', '', 2963, 'https://dailyconf.com/2963-autosave-v1/', 0, 'revision', '', 0); 
