INSERT INTO `dc_posts` VALUES (3002, 1, '2018-09-03 13:53:42', '2018-09-03 10:53:42', 'DevGAMMis the biggest conference for game industry professionals in Russia, Ukraine and Belarus. The event offers quality content about game development, low entry threshold for beginner developers as well as a series of activities meant for productive pastime and effective networking.\r\nDevGAMM Showcaseis an opportunity to showcase your game to conference attendees, make a name for your project in the gaming industry, have your game playtested, get feedback from the industry professionals, attract potential publishers, and even find your first fans.\r\n\r\n[embed width="640" height="360"]\r\nhttps://www.youtube.com/watch?v=Gi8DyeZdaas\r\n[/embed]', 'DevGAMM Minsk', '', 'inherit', 'closed', 'closed', '', '2958-revision-v1', '', '', '2018-09-03 13:53:42', '2018-09-03 10:53:42', '', 2958, 'https://dailyconf.com/2958-revision-v1/', 0, 'revision', '', 0); 
