INSERT INTO `dc_posts` VALUES (3014, 1, '2018-09-03 14:03:58', '2018-09-03 11:03:58', 'The Montreal International Game Summit brings together hundreds of international experts to one of the largest hubs of video games development in the world so that they can share their knowledge with the local and international developers attending the event.Learn from the experts during conference sessions, masterclasses and one-on-one coaching sessions before rubbing shoulders with them during the MIGS networking cocktails and other special events over the 2 days.\n\nUnlike many other events in the industry, this really is an opportunity for attendees to meet with experts such as Ian Livingstone (Games Workshop), Amy Hennig (Electronic Arts), Brandon Beck (Riot Games), Marty ODonnell (Compositor for Halo and Destiny), Ray Musika (Bioware) and many others who have joined us as speakers.\n\n[embed width="640" height="360"]\nhttps://www.youtube.com/watch?v=egmyXnkpCzU\n[/embed]', 'Montreal International Game Summit (MIGS)', '', 'inherit', 'closed', 'closed', '', '2955-autosave-v1', '', '', '2018-09-03 14:03:58', '2018-09-03 11:03:58', '', 2955, 'https://dailyconf.com/2955-autosave-v1/', 0, 'revision', '', 0); 
