INSERT INTO `dc_posts` VALUES (3048, 1, '2018-09-03 15:11:22', '2018-09-03 12:11:22', '<strong>Games Con</strong> is returning in October 2018 to deliver a world of innovation, and the ultimate gaming experience. Organised by the team that bring you Middle East Film &amp; Comic Con (MEFCC), this will be the 2nd edition of the GCC\'s largest dedicated gaming event that will bring the region\'s vibrant and ever-growing community together.Be a part of the Middle East\'s biggest gaming event! Join the regions gaming fanatics, become your favourite characters and indulge in a world of immersive experiences and entertainment.\n\nMeet YouTube stars and watch your online-world come to life in an oasis for tech, art and character enthusiasts - coming together to celebrate a love for all things gaming &amp; anime.\n\n[embed width="640" height="360"]\nhttps://youtu.be/kQ8IczwoAe4\n[/embed]', 'Middle East Games Con', '', 'inherit', 'closed', 'closed', '', '2944-autosave-v1', '', '', '2018-09-03 15:11:22', '2018-09-03 12:11:22', '', 2944, 'https://dailyconf.com/2944-autosave-v1/', 0, 'revision', '', 0); 
