INSERT INTO `dc_posts` VALUES (3091, 1, '2018-09-03 15:52:49', '2018-09-03 12:52:49', 'Scotlands No1 gaming festival, <strong>Resonate Total Gaming</strong>, is back for the third year in Glasgow on 19-21 October.\r\n\r\nLast year more than 9500 gamers flocked to the festival, which is organised by QD Events.For 2018, the event has increased in capacity and will include 3 feature packed halls (one of which is for over 18s only) an eSports arena, BAFTA led workshops, YouTube celebrities, including Syndicate, and a number of dedicated zones such as the Kids Zone, Retro Zone, and Indie Zone where established and local independent developers have the chance to showcase their latest demos and releases.', 'Resonate Total Gaming Festival', '', 'inherit', 'closed', 'closed', '', '2931-revision-v1', '', '', '2018-09-03 15:52:49', '2018-09-03 12:52:49', '', 2931, 'https://dailyconf.com/2931-revision-v1/', 0, 'revision', '', 0); 
