INSERT INTO `dc_posts` VALUES (3116, 1, '2018-09-03 16:49:14', '2018-09-03 13:49:14', 'The UK Best Places To Work Awards returns for a second year at the Ham Yard Hotel in London. Celebrate employer excellence at a special ceremony held in the heart of city.\n\nWe\'ll be highlighting the greatest games industry companies, alongside excellence in diversity, corporate social responsibility, leadership, training, wellbeing and a whole host more.\n\nWinners will receive our prestigious Best Places badge, while those who do not make the cut will receive a free report (upon request) on anything we\'ve managed to pick up during the judging process (participation in the awards is strictly confidential for anyone outside of the winners).\n\nThe awards is free to take part in, and the event itself will include a drinks reception, ceremony and after awards networking opportunities.', 'Best Places To Work Awards', '', 'inherit', 'closed', 'closed', '', '2921-autosave-v1', '', '', '2018-09-03 16:49:14', '2018-09-03 13:49:14', '', 2921, 'https://dailyconf.com/2921-autosave-v1/', 0, 'revision', '', 0); 
