INSERT INTO `dc_posts` VALUES (3252, 1, '2018-09-07 12:16:22', '2018-09-07 09:16:22', '<h3><strong>Real World Robotic Implementation Strategies</strong></h3>\r\nThe Chief Robotics Officer (CRO) Summit is a conference designed for professionals whose responsibility is evaluating and managing the deployment of automation, robotics and the latest intelligent technologies into their organizations. The Summit delivers in-depth educational sessions, lead by leading experts and technology providers in a highly interactive format that enhances connections to peers.\r\n<p class="p1"><b>Conference Sessions</b></p>\r\n\r\n<ul class="ul1">\r\n 	<li class="li1">Part One: Leading the Change: Digital Transformation &amp; the Workforce of the Future</li>\r\n 	<li class="li1">Part Two: Formulating a Workforce Reprogramming Strategy for Your Organization</li>\r\n 	<li class="li1">Creating a Metrics Structure to Measure ROI</li>\r\n 	<li class="li1">Robots and SMMs (Small and Medium Manufacturers) Successes and Challenges</li>\r\n 	<li class="li1">Part One: Successfully Implementing Your Automation Strategy</li>\r\n 	<li class="li1">Part Two: Establishing an Infrastructure to Monitor Results</li>\r\n 	<li class="li1">Defining What You Want to Solve and Securing Management Buy-In</li>\r\n 	<li class="li1">Cobot and the Small Business: Identifying What You Want and Where to Start</li>\r\n 	<li class="li1">Implementing Automation: What are the Political and Economic Considerations?</li>\r\n 	<li class="li1">AGV and IIoT Integration Strategies to Gain Agility and Competitive Advantage</li>\r\n 	<li class="li1">Robotic Roadworks and Excavation System</li>\r\n 	<li class="li1">Demystifying the Use of Frameless Motors in Robotics</li>\r\n</ul>', 'CRO Summit (by RoboBusiness)', '', 'publish', 'open', 'closed', '', 'cro-summit', '', '', '2018-09-07 12:16:34', '2018-09-07 09:16:34', '', 0, 'https://dailyconf.com/?post_type=event&#038;p=3252', 0, 'event', '', 0); 
